catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') {
        return;
    }

    // Remove caracteres não numéricos (pontos, traços, etc)
    var cpf = newValue.replace(/[^\d]+/g, '');

    // Verifica se o CPF tem 11 dígitos
    if (cpf.length !== 11) {
        alert('CPF inválido! O CPF deve conter 11 dígitos.');
        g_form.clearValue('sys_class_cpf'); // Limpa o campo CPF
        return;
    }

    // Validação de CPF
    var soma;
    var resto;
    soma = 0;

    if (cpf == "00000000000" || cpf == "11111111111" || cpf == "22222222222" || 
        cpf == "33333333333" || cpf == "44444444444" || cpf == "55555555555" || 
        cpf == "66666666666" || cpf == "77777777777" || cpf == "88888888888" || 
        cpf == "99999999999") {
        alert('CPF inválido!');
        g_form.clearValue('sys_class_cpf');
        return;
    }

    for (var i = 1; i <= 9; i++) {
        soma += parseInt(cpf.substring(i - 1, i)) * (11 - i);
    }

    resto = (soma * 10) % 11;

    if ((resto == 10) || (resto == 11)) {
        resto = 0;
    }

    if (resto != parseInt(cpf.substring(9, 10))) {
        alert('CPF inválido!');
        g_form.clearValue('sys_class_cpf');
        return;
    }

    soma = 0;
    for (var j = 1; j <= 10; j++) {
        soma += parseInt(cpf.substring(j - 1, j)) * (12 - j);
    }

    resto = (soma * 10) % 11;

    if ((resto == 10) || (resto == 11)) {
        resto = 0;
    }

    if (resto != parseInt(cpf.substring(10, 11))) {
        alert('CPF inválido!');
        g_form.clearValue('sys_class_cpf');
        return;
    }


}
